  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Records of result
-- ----------------------------
INSERT INTO `result` VALUES ('2E5CD74128044345A52C3A7F24A5E3D6', '章丘百脉泉公园', '百脉泉公园，位于济南市章丘明水境内，这里是龙山文化的发祥地，一代词宗李清照的故里。景区以群泉自然喷涌成湖而著称，景区内群泉鼎沸，杨柳染烟，画廊奇阁，宛如画卷，其中百脉泉、梅花泉、墨泉、东麻湾（万泉湖）被评为济南新七十二名泉。“百脉寒泉珍珠滚”，居章丘八景之首，是中国北方独具特色的泉景景区，享有“天下奇观”的美誉。景区建有儿童乐园区、名泉水景区、花园景树区、幽静学习区、密林散步区、龙泉寺、清照园、清照词园。景区层次变化丰富，意韵含蓄深远，步移景异，引人入胜。为纪念生于百脉泉畔的宋代女词人李清照而修建的清照园，以自然景观最美，建筑规模最大，文化品位最高，馆藏资料最全居全国四座李清照纪念馆之首。始建于明景泰元年的龙泉寺内，“品”形百脉泉涌珠浮翠，梵王宫大殿庄严雄伟，是泉水文化、历史文化、宗教文化有机融合的名胜景点。', NULL, '2018-06-11 23:29:41', 2, NULL, '680E70B57B1E47A1812354BA235C078C');
INSERT INTO `result` VALUES ('30184D516D5A4A88BBEA4F3E47D04B43', '济南“抖”起来，泰山挤油油 散客出游时代，游客爱上“好客山东”', '随着私家车的日益普及和“五一”期间高速公路免收小型客车通行费，自驾游成为假日旅游的主角。近郊游、周边游、乡村游、亲子游火爆，以山水踏青、乡村体验为主的城市周边游需求旺盛。　　“打卡”济南景点成时尚　　今年“五一”，全省范围内最火爆的城市不是泰安和青岛,而是一直有些温吞的济南。　　一首陈小熊的《济南济南》，成为', '济南 好客山东', '2018-06-11 23:46:36', 1, 'DB543854DCE44C38BCB1A2AEA54056F8', NULL);
INSERT INTO `result` VALUES ('44035777A2BC4B27B2A7AC9CE6A5FE8A', '心灵的震颤', '心灵的震颤——“我和泰山的故事”征文活动参赛作品    作者：赵谦       \n记得上世纪九十年代，在上海读大学的时候，同学问我：“你来自泰安，住在泰山脚下，一定经常爬泰山吧？”我颇为自豪地说那当然，爬了很多次呢。他们羡慕地问我，泰山一定很美吧。我说那还用说吗，美不胜收。把他们一个个说得热血沸腾。其实，说完', '心灵', '2018-06-11 23:48:32', 0, '6CD28B80B9A4412DBCA10E159A901652', NULL);
INSERT INTO `result` VALUES ('4E3495ED0A3F46FEA280F520F3C23545', '济南之行', '最后一次因为有你而去的这个城市了，正因为我们过得很开心，所以时间才会显得如此短暂。离别的不舍，是为了下一次更好的相聚，下次再回济南，应该就是去旅游了，到时候带上你开发的这个应用，我们一起去自驾游。', '自驾游 回忆 济南', '2018-06-11 23:10:18', 0, '7C60B61649784ACA83A8D298CF63157E', NULL);
INSERT INTO `result` VALUES ('52D5B4694AE34097A4F7C32132BD1A35', '应用评价', '很好的一款应用，特别棒的你，加油！', '应用 评价', '2018-06-11 23:10:49', 0, 'B9BDEFABF6CC4B2B9EB771BB1F46A0F2', NULL);
INSERT INTO `result` VALUES ('556846EB628149FE81F0AF74B89973A4', '日照旅游：靓展济南旅交会', '“2018山东（济南）国际旅游交易会（简称旅交会）”5月25——27日在济南盛大举行，市旅发委组织带领各区县旅游局及日照文旅集团、海洋文旅、海滨国家森林公园、浮来山景区、五莲山景区、日照海洋公园、阳光海岸露营公园等3O余家旅游企事业单位近100人组团参展借势营销。这是继4月份日照市在济南召开旅游推介会之后，日', '日照 旅游', '2018-06-11 23:44:41', 1, '31C6B9E981A74E908DBB7736ABBFBEFC', NULL);
INSERT INTO `result` VALUES ('5F381485DD3142E19D6CFC1BECFDF1D7', '我与泰山有个缘', '我与泰山有个缘——“我和泰山的故事”征文活动参赛作品  作者：赵青       \n我从小就生活在泰山脚下，一出家门就能看到泰山。小时候常常跟着父母到山脚下洗衣服、摸小鱼、捉螃蟹、采蘑菇、拾松塔，通常一玩儿就是一天，太阳落山时才依依不舍的回家去。泰山就像慈祥的老祖父端坐在那里，而我就在他的肩头、膝下，攀爬着、嬉', '济南 泰山', '2018-06-11 23:47:59', 0, '88B311107E5C43738008C3F1986E8BD5', NULL);
INSERT INTO `result` VALUES ('6705502424154D17914D029EF66B3B0F', '展现泉城范儿！百花洲北入口打造济南老院，下月完工', '2016年6月1日,百花洲一期项目正式开园,至今已经整整两年,逐渐为济南市民所青睐。其间百花洲旅游项目逐步丰富,但是北入口却略显简陋。今年5月5日,北入口景观提升工程正式开工,7月20日将完工,目前工期将近过半。北入口景观提升,就是要展示泉城特色风貌,提升整个明府城的文化旅游价值。　　目前工期近半，面积两三百', '济南 百花洲 泉城', '2018-06-11 23:40:00', 1, 'B4F444C22F214DCAB6207E3B873B1689', NULL);
INSERT INTO `result` VALUES ('6A89E3292F6842DAACA4C387ADAEF9D3', '爱上泰山的山，爱上泰山的人', '爱上泰山的山，爱上泰山的人——“我和泰山的故事”征文活动参赛作品  作者：吕晓明       2007年7月20日，那天对于我来说是一个非常特别的日子。       \n在那一天，我又一次来到了泰山的玉皇顶。在玉皇庙的同心锁链的旁边，我一个人矗立了很久很久。望着我和东旭的那把早已经被淹没在锁群里的同心锁，一股莫', '泰山的山 泰山的人', '2018-06-11 23:52:07', 0, '17ED08D7134B4F8995B7C50FF0AEEA17', NULL);
INSERT INTO `result` VALUES ('7633D6E1DFD4463FB9B4892830F9B867', '济南红叶谷生态文化旅游区 ', '红叶谷生态文化旅游区位于济南市历城区锦绣川乡南部山区，是一处集游览观光、休闲、科普、娱乐、赏红叶等于一体的南部山区旅游景点。红叶谷以野生灌木丛黄栌为主，是红叶谷独有的观赏树种。红叶谷海拔500米左右，平均气温比市区低5摄氏度左右，山地小气候较为明显，景区的植被覆盖率达97%，空气中负氧离子的含量是市区的300多倍，是一处“天然氧吧”，故称红叶谷是“泉的源头、云的故乡、花的世界、林的海洋、休闲度假的天堂”。 红叶谷一谷有四季，四季不同天，是一年四季休闲度假的好去处。春天，杂花生树，群莺乱飞，红的碧桃，白的梨花，黄的连翘星罗棋布的点缀在一片生机盎然的山林中；夏日，谷中一片心动的郁郁葱葱，飞瀑湍流，山风送爽，谷长景深，流水潺潺，是难得的避暑胜地：秋天，登高送目，万山红遍，层林尽染，美不胜收；冬日，白雪皑皑，玉树琼花，阳光纯净而明媚。', NULL, '2018-06-11 23:26:07', 2, NULL, '6AED583364214F5A9742C9071752054F');
INSERT INTO `result` VALUES ('765163E38CA748338440FC1D425EE0BE', '济南大明湖风景名胜区 ', '大明湖是济南三大名胜之一，位于老城区东北，曾名“莲子湖”、“西湖”，金代始称大明湖。明代重修城墙，大明湖遂成今日形貌。大明湖由城内众泉汇流而成，水质清冽，游鱼可见，平均水深2米左右。沿湖800余株垂柳环绕和40余亩白荷红莲，构成了“四面荷花三面柳，一城山色半城湖”的秀美画卷。济南八景中的“鹊华烟雨”、“汇波晚照”、“佛山倒影”、“明湖秋月”均可在大明湖上观赏。大明湖园内名胜遍布，计有一阁、二园、四祠、十亭，还有书画展览馆、碑刻展等人文景观。大明湖新区于2009年扩建，免费开放。主要标志性景点有超然楼、曾堤、水云居等。其中，小东湖风格别具，为“新明湖”的重要景观，是护城河游船进入大明湖的必经之路。', NULL, '2018-06-11 23:23:06', 2, NULL, '3DC57565428F4A02AE34299E20288FF9');
INSERT INTO `result` VALUES ('8C2594BD755740D0AB0262152270B95E', '济南灵岩寺旅游区 ', '灵岩寺，始建于东晋，佛教底蕴丰厚，距今己有1600多年的历史。自唐代起与浙江国清寺、南京栖霞寺、湖北玉泉寺并称“海内四大名刹”，并名列其首。灵岩寺旅游区群山环抱、岩幽壁峭；柏檀叠秀、泉甘茶香；古迹荟萃、佛音袅绕。不仅有高耸入云的辟支塔，传说奇特的铁袈裟；亦有隋唐时期的般舟殿，宋代的彩色泥塑罗汉像；更有“镜池春晓”、“方山积翠”、“明孔晴雪”等自然奇观。故明代文学家王世贞有“灵岩是泰山背最幽绝处，游泰山不至灵岩不成游也”之说。', NULL, '2018-06-11 23:33:06', 2, NULL, 'FA2D0B993F0740E389B6950D8EF4F293');
INSERT INTO `result` VALUES ('9039FCFD139E48C58BE843404C4D281B', '浓墨重彩看泰山——宋遂良', '早就听说泰安有个《泰山封禅》的山水实景演出很精彩,并有“游泰山,必看封禅”的说赞。因为以桂林、丽江、西湖“印象”为代表的这类演出近年来遍及大江南北、塞北秦川,我也看过一些,物以多为贱,也就迟迟未去问津。不料今年却有幸陪客人连看了两遍,来客大加赞赏。因为都是些见过世面的文化人,他们的肯定也使我这个在泰安住过20', '浓墨重彩 泰山', '2018-06-11 23:51:10', 0, '6B407563851E403994577848BA5F693A', NULL);
INSERT INTO `result` VALUES ('9074A69C57AC45409E0A018186F33527', '从神话传说探秘泰山玉', '近期，一则有关泰安玉石的新闻吸引了很多读者的关注，在泰山西麓初步勘探出一个储量大约为900万吨左右的玉矿带。泰山玉早已有之，但这次的发现能为泰山玉带来什么？泰山玉究竟蕴含着哪些历史文化元素？泰山玉为何会引起这么多人的关注？带着这些问题，记者又一次来到泰山玉的出产地——泰安，试图找到这些问题的答案。　　 500', '神话传说 泰山玉', '2018-06-11 23:50:19', 0, '8980CB959131421CAF1D26AC83AF0C38', NULL);
INSERT INTO `result` VALUES ('9350E2C446AB42F499D4F20541413738', '济南解放阁逢周一闭馆！免费参观要凭本人有效证件！', '解放阁是济南战役攻城突破口遗址，山东省文物保护单位，山东省爱国主义教育基地，山东省革命传统教育基地。　　近年来，解放阁日均客流量近千人次，周末接待团队较多，日客流量可达3000人次。展室设备长期处于超负荷运转状态，室内展橱、照明灯、触摸屏、解说文字等都需要日常维护保养。　　为加强解放阁保护，提升管理水平，确保', '济南 解放阁', '2018-06-11 23:43:00', 1, '589A18ED254E43A79CACBC7C0BCECECF', NULL);
INSERT INTO `result` VALUES ('98397C5DFB1E4E4B99011F74B3B0142A', '测试一下', '123456789', 'test', NULL, 0, '26A3AD17AEC74D4BA05653EA1641B039', NULL);
INSERT INTO `result` VALUES ('989C5FACAF4E4554B5C6A6E6399CC1A7', '测试账户', '测试一下，是不是好使！挺好使的哈！', '测试 好使', NULL, 0, 'FFAA0E846FA241FB996748147E6536E4', NULL);
INSERT INTO `result` VALUES ('9C8B3831F41E4A2BBDF062596674514E', '济南九如山瀑布群风景区', '九如山瀑布群风景区是国家AAAA级景区、国家森林公园，位于山东省济南市历城区西营镇，地处泰山北麓，东临章丘，南接莱芜和泰安，距离济南市区48千米。九如山素有“一步跨两国（齐国、鲁国），一脚踏四地（历城、章丘、莱芜、泰安）”之称。景区内自然资源得天独厚，原始景观独具特色。36平方千米的连绵群山，峰峦叠嶂，峡谷纵横，森林茂密，山泉遍布，溪流交错，瀑布成群。这些令人震撼的景观由长达10千米的实木栈道巧妙连缀在一起，形成了以八潭、九瀑、二十四泉、三十六峰为核心景观，以山峰、栈道、山泉、溪流、瀑布、深潭为特色的中国北方罕见的大型生态自然景区。同时它又是“泉城七十二名泉”的发源地——济南南部山区三川之一锦绣川的源头。现在已经建成的有九如峡谷、天蓬瀑布、深潭飞瀑、长城古道四大景观区域。', NULL, '2018-06-11 23:18:29', 2, NULL, '138B9FF8676D4E62AC36F6A6E072E9DD');
INSERT INTO `result` VALUES ('CDC33C955D3A4E4282F0BCEDB36CE4E2', '济南济阳县乡村旅游正当时', '日前，济阳县首次亮相2018山东（济南）国际旅游交易会，会上，济阳展区对当地旅游线路进行了集中推介，众多乡村旅游特色商品的展卖受到现场观众的追捧。　　济阳作为省城近郊县，济阳具有发展旅游业的区位优势和资源优势，乡村旅游资源禀赋较好。用好资源，走好属于济阳自己的路，是济阳旅游跨越式发展的重要保证。　　资源优势得', '济南 济阳县 乡村旅游', '2018-06-11 23:42:24', 1, '8BD1459569DC448381C9E832C16A8959', NULL);
INSERT INTO `result` VALUES ('CDE7F74E26BB49BAA5C7529E1A621A92', '济南正从门票经济向旅游产业经济转型', '今年“五一”假期，提升后免费开放的济南国际园博园迎来了超过20万的游客，这个数字能与园博园9年前开园时媲美。去年元旦开始免费的大明湖也已经交出了人气答卷，连续两年“五一”客流都比免费前增加数倍。在从门票经济向旅游产业经济转型的过程中，济南已经看到了旅游业发展的新希望。　　免费后单日入园人次　刷新开园以来纪录　', '济南 正从门 经济转型', '2018-06-11 23:45:52', 1, '3DC5F28771314019A8E2B822A5372F57', NULL);
INSERT INTO `result` VALUES ('EB8C987C49A14AC3861874D4A88B71F9', '济南市旅游协会第五届会员大会暨济南市旅游联合会成立大会在济南召开', '5月31日，济南市旅游协会召开第五届会员代表大会暨济南市旅游联合会成立大会。济南市旅游发展委员会主任郅良、副主任闫险峰出席大会，省旅游协会王德刚会长、尚玉轩秘书长到会祝贺。会议选举山东省交通运输集团董事长吴宗昌为联合会会长。　　会议审议通过了济南市旅游协会第四届理事会工作报告、济南市旅游协会章程修稿草案，完成', '济南市 旅游联合会 第五届', '2018-06-11 23:40:59', NULL, 'C98D7BD88B424D768337C5188067D5BB', NULL);
INSERT INTO `result` VALUES ('FCBDC2E3729B493B90B65CE9BFEA7D68', '郭沫若四赋泰山', '1961年5月，已逾古稀之年的郭沫若，登游泰山。在两天的游程里，抒发豪情壮志，赞美泰山壮丽景色，并赋诗四首。     \n登山一路，郭老兴致很高，不顾磴道险峻，仍然谈笑风生，说古论今，观览沿途风光，赞叹不止。中天门小憩后，继续攀登，穿步云桥，观五大夫松，赏松山。只见双峰对峙，绝壁生松，层层叠叠。突然，乌云穿行其', '郭沫若 泰山', '2018-06-11 23:49:16', 0, 'E5E05906AD5E44938E0D2F886D1D8C4F', NULL);
INSERT INTO `result` VALUES ('FE9B6BA46938451FA971830281515CC5', '济南华侨城项目开工，章丘两大文旅项目全面展开', '“济南华侨城项目是继明水古城之后，章丘区开工的又一重大文旅项目，也标志着带动章丘区城市产业转型的‘五朵金花’全面开工。”5月28日，在济南华侨城项目开工仪式上，章丘区委书记刘天东说，这一项目开工，将有力增强章丘的商业、会展、体育、文化演艺等服务功能，形成建设省会城市群CAZ的重要窗口，带动章丘新旧动能转换大步', '济南 华侨城 章丘 文旅', '2018-06-11 23:43:51', 1, '6676DA2FD6834664800D05BBC9723905', NULL);

-- ----------------------------
-- Table structure for role
-- ----------------------------
DROP TABLE IF EXISTS `role`;
CREATE TABLE `role`  (
  `ID` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `ROLENAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `ISUSED` int(11) NULL DEFAULT NULL,
  `DETAIL` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for role_resource_mapping
-- ----------------------------
DROP TABLE IF EXISTS `role_resource_mapping`;
CREATE TABLE `role_resource_mapping`  (
  `ID` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `ROLE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `RESOURCE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`ID`) USING BTREE,
  INDEX `FKeg1k8vlsp42uks2v7fojp6bys`(`ROLE`) USING BTREE,
  INDEX `FK8j7w2n6ivwyxad0dlonglsmg8`(`RESOURCE`) USING BTREE,
  CONSTRAINT `FK8j7w2n6ivwyxad0dlonglsmg8` FOREIGN KEY (`RESOURCE`) REFERENCES `resource` (`ID`) ON DELETE RESTRICT ON UPDATE RESTRICT,
  CONSTRAINT `FKeg1k8vlsp42uks2v7fojp6bys` FOREIGN KEY (`ROLE`) REFERENCES `role` (`ID`) ON DELETE RESTRICT ON UPDATE RESTRICT
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

-- ----------------------------
-- Table structure for scenicspot
-- ----------------------------
DROP TABLE IF EXISTS `scenicspot`;
CREATE TABLE `scenicspot`  (
  `ID` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `NAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `TAGLINE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `LEVEL` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `DEFAULTPHOTO` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `TICKETPRICE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `INTRO` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `CNAME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `FEATURE` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `LINE` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `TEL` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `GUIDEMAN` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `ADDRESS` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `TIME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `SUPPORTING` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `COSTTIME` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `WEBSITE` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `SERVICECENTER` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `PRICEDESCRIPTION` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `DISCOUNT` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `BUS` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `DRIVING` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `PARK` varchar(1000) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `SIGN` int(11) NULL DEFAULT NULL,
  `RELEASEDATE` datetime(0) NULL DEFAULT NULL,
  `MANAGEMENT` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`ID`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

